<script>
import { ref, onMounted } from "vue";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default {
    name: "Map",
    setup() {
        const mapContainer = ref(null);
        let mapInstance = null;

        onMounted(() => {
            mapInstance = L.map(mapContainer.value).setView(
                [48.9361, 2.3319],
                15.4
            );

            L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
                maxZoom: 20,
            }).addTo(mapInstance);
        });

        const ajouterMarqueur = () => {
            if (mapInstance) {
                mapInstance.on("click", (event) => {
                    const marker = L.marker(event.latlng, {
                        icon: getMarkerIcon(selectedColor.value),
                    }).addTo(mapInstance);
                    markers.value.push(marker); // Ajoute le marqueur à la liste
                });
            }
        };

        // Fonction pour obtenir une icône de marqueur avec une couleur spécifique

        const filtrerMarqueurs = (color) => {
            markers.value.forEach((marker) => {
                if (marker.options.icon.options.html.includes(color)) {
                    marker.addTo(mapInstance);
                } else {
                    mapInstance.removeLayer(marker);
                }
            });
        };

        return {
            mapContainer,
        };
    },
};
</script>

